    const x2 = sqrnmul(x1, 1, x1, p);
    const r0 = this.core(x1, x2);
    const r1 = sqrn(r0, 1, p);
    const r2 = sqrnmul(r1, 1, x1, p);
    const r3 = sqrn(r2, 1, p);
    const r4 = sqrnmul(r3, 2, x2, p);

