    // Exponent: (p + 1) / 4
    // Bits: 224x1 222x0
    const p = this.p.n;
    const x2 = sqrnmul(x1, 1, x1, p);
    const r0 = this.core(x1, x2);
    const r1 = sqrnmul(r0, 2, x2, p);
    const r2 = sqrn(r1, 222, p);

    return r2;
